        private readonly Biller.BillerClient _billerClient;
        private readonly Coiner.CoinerClient _coinerClient;
        private readonly IEmailSender _emailSender;
        private readonly ILogger<PaymentsController> _logger;
        public PaymentsController(Biller.BillerClient billerClient, Coiner.CoinerClient coinerClient, IEmailSender emailSender, ILogger<PaymentsController> logger)
        {
            _billerClient = billerClient;
            _coinerClient = coinerClient;
            _emailSender = emailSender;
            _logger = logger;
        }
        [AllowAnonymous]
        public async Task<IActionResult> CreateInvoice()
        {
            if (!User.Identity.IsAuthenticated)
            {
                return RedirectToAction("Login", "Authentication", new { redirectUrl = "/payments/createinvoice" });
            }
            try
            {
                string userId = User.Identity.Name;
                string orderId = ($"{userId}{DateTime.Now.Ticks}").ToSha256();
                string email = User.FindFirstValue("email");
                NewInvoiceResponse newInvoiceResponse = await _billerClient.MakeInvoiceAsync(
                    new NewInvoiceRequest
                    {
                        Amount = "2",
                        Currency = "USD",
                        ServiceId = 1,
                        RedirectUrl = $"https://dinglevalley.net/payments/confirm?orderId={orderId}",
                        OrderId = orderId
                    });
                if (!newInvoiceResponse.Successful)
                {
                    _logger.LogError("Could not create invoice on btcpay");
                    return View();
                }
                MakeInvoiceResponse logInvoice = await _coinerClient.MakeInvoiceAsync(
                    new MakeInvoiceRequest
                    {
                        InvoiceId = newInvoiceResponse.InvoiceId,
                        UserId = userId,
                        Email = email,
                        OrderId = orderId
                    });
                if (!logInvoice.Succeeded)
                {
                    _logger.LogError("Could not create invoice on coindb");
                    return View();
                }
                return Redirect(newInvoiceResponse.CheckOutUrl);
            }
            catch (Exception ex)
            {
                _logger.LogError($"An exception was thrown when creating invoice: {ex.Message}");
                return View();
            }
        }
